#' Survival Analysis
#'
#' 
#' @param data .
#' @param event .
#' @param eventLevel .
#' @param elapsed .
#' @param groups .
#' @param ci .
#' @param cens .
#' @return A results object containing:
#' \tabular{llllll}{
#'   \code{results$summary} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$tests} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$sc} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$hf} \tab \tab \tab \tab \tab an image \cr
#' }
#'
#' Tables can be converted to data frames with \code{asDF} or \code{\link{as.data.frame}}. For example:
#'
#' \code{results$summary$asDF}
#'
#' \code{as.data.frame(results$summary)}
#'
#' @export
surv <- function(
    data,
    event,
    eventLevel,
    elapsed,
    groups,
    ci = FALSE,
    cens = TRUE) {

    if ( ! requireNamespace('jmvcore'))
        stop('surv requires jmvcore to be installed (restart may be required)')

    options <- survOptions$new(
        event = event,
        eventLevel = eventLevel,
        elapsed = elapsed,
        groups = groups,
        ci = ci,
        cens = cens)

    results <- survResults$new(
        options = options)

    analysis <- survClass$new(
        options = options,
        data = data)

    analysis$run()

    analysis$results
}
